fig_alligator.style:
body {
  height: 100vh;
  width: 100vw;
  background: #eaed5c;
  background: -webkit-radial-gradient(right 60%, #caed5c, #f9c534);
  background: radial-gradient(at right 60%, #caedfc, #f9c534);
  display: -webkit-box;
  display: -webkit-flex;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-align: center;
  -webkit-align-items: center;
  -ms-flex-align: center;
  align-items: center;
  display: flex;
  -webkit-box-align: center;
  -webkit-align-items: center;
  -ms-flex-align: center;
  align-items: center;
  margin: 0;
}

.contenedor {
  position: relative;
  width: 80vw;
  height: 80vw;
  margin: 90px auto;
}

.aligator {
  position: absolute;
  margin: 0 50px
}

.cabeza {
  position: absolute;
  width: 21vw;
  height: 19.9vw;
  border-radius: 100vw;
  background: #75b000;
  margin: 16vw 27vw;
  animation: rotar7 1.2s alternate infinite;
  -moz-animation: rotar7 1.2s alternate infinite;
  -webit-animation: rotar7 1.2s alternate infinite;
}

.boca {
  position: absolute;
  border-bottom: 23vw solid #75b000;
  border-left: 3.5vw solid transparent;
  border-right: 3vw solid transparent;
  height: 0;
  width: 4.5vw;
  transform: rotate(75deg);
  -moz-transform: rotate(75deg);
  -webkit-transform: rotate(75deg);
  margin: -6vw 19vw
}

.boca::before {
  content: "";
  display: block;
  width: 4.5vw;
  height: 4vw;
  border-radius: 100%;
  background: #75b000;
  margin: -2vw 0vw
