.boca::after {
  content: "";
  display: block;
  width: 4vw;
  height: 21vw;
  border-radius: 100%;
  border-right: 1vw solid #354405;
  margin: -3vw 1.3vw
}

.dientes {
  position: absolute;
  width: 0;
  height: 0;
  border-left: 1.2vw solid transparent;
  border-right: 1.2vw solid transparent;
  border-top: 4vw solid white;
  margin: 6.5vw 30vw;
  transform: rotate(-21deg);
  -moz-transform: rotate(-21deg);
  -webkit-transform: rotate(-21deg);
}

.dientes::before {
  content: "";
  position: absolute;
  width: 0;
  height: 0;
  border-left: 1.2vw solid transparent;
  border-right: 1.2vw solid transparent;
  border-top: 4vw solid white;
  margin: -4vw 1.5vw;
}

.nariz {
  position: absolute;
  width: 2vw;
  height: 2vw;
  border-radius: 100vw;
