}

.nariz::before {
  content: "";
  position: absolute;
  width: 2vw;
  height: 2vw;
  border-radius: 100vw;
  background: #354405;
  margin: -2vw 0vw
}

.boca3 {
  position: absolute;
  width: 25vw;
  height: 4.5vw;
  border-radius: 0 0 172vw 0vw;
  background: #c4b400;
  transform: rotate(-21deg);
