  border-bottom: 12vw solid #75b000;
  margin: 13vw 1vw;
  transform: rotate(50deg);
  -moz-transform: rotate(50deg);
  -webkit-transform: rotate(50deg);
  animation: rotar3 1.2s alternate infinite;
  -moz-animation: rotar3 1.2 alternate infinite;
  -webit-animation: rotar3 1.2s alternate infinite;
}

.cola::before {
  content: "";
  position: absolute;
  width: 40vw;
  height: 29vw;
  border-radius: 100%;
  border-bottom: 5vw solid #c4b400;
  margin: 13.5vw 4vw;
