  -webkit-transform: rotate(12deg);
}

.c3 {
  margin: 33vw 23vw
}

.c1::before,
.c2::before,
.c3::before {
  content: "";
  position: absolute;
  width: 5vw;
  height: 3vw;
  border-radius: 30vw 30vw 0 0;
